// components/CardMovies.js
import React from 'react';
import { View, Text, StyleSheet } from 'react-native';
import styles from './styles'; // Certifique-se de que o caminho está correto

const CardMovies = ({ titulo, autor, descricao, editora, faixaEtaria }) => {
  return (
    <View style={styles.card}>
      <Text style={styles.title}>{titulo}</Text>
      <View style={styles.dados_livro}>
        <Text style={styles.textCommon}>Autor: {autor}</Text>
        <Text style={styles.textCommon}>Descrição: {descricao}</Text>
        <Text style={styles.textCommon}>Editora: {editora}</Text>
        <Text style={styles.textCommon}>Faixa Etária: {faixaEtaria}</Text>
      </View>
    </View>
  );
};

export default CardMovies;
