import React from 'react';
import { View, Text, TouchableOpacity } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import styles from './styles'; 
import CardMovies from '../../components/cardMovies/style'; // Adaptar este componente para exibir livros

export default function Details() {
  const route = useRoute();
  const navigation = useNavigation();
  const { titulo, autor, descricao, editora, faixaEtaria } = route.params || {};

  return (
    <View style={styles.viewContainer}>
    
      <CardMovies
      />
      <Text style={styles.title}>{titulo}</Text>
      <Text style={styles.textCommon}>Autor: {autor}</Text>
      <Text style={styles.textCommon}>Descrição: {descricao}</Text>
      <Text style={styles.textCommon}>Editora: {editora}</Text>
      <Text style={styles.textCommon}>Faixa Etária: {faixaEtaria}</Text>
      
     
    </View>
  );
}
