// Details.js
import React from 'react';
import { View, Text, TouchableOpacity } from 'react-native';
import { useNavigation, useRoute } from '@react-navigation/native';
import styles from './styles'; // Certifique-se de que este caminho está correto

export default function Details() {
  const route = useRoute();
  const navigation = useNavigation();
  const { titulo, autor, descricao, editora, faixaEtaria } = route.params || {};

  return (
    <View style={styles.viewContainer}>
      <Text style={styles.title}>{titulo}</Text>
      <Text style={styles.author}>Autor: {autor}</Text>
      <Text style={styles.description}>Descrição: {descricao}</Text>
      <Text style={styles.publisher}>Editora: {editora}</Text>
      <Text style={styles.ageGroup}>Faixa Etária: {faixaEtaria}</Text>

      <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
        <Text>Voltar</Text>
      </TouchableOpacity>
    </View>
  );
}
