import { useNavigation, useRoute } from '@react-navigation/native'
import React from 'react'
import {View, Text,Image, TouchableOpacity} from 'react-native'
import styles from './styles';
import { AntDesign } from '@expo/vector-icons';
import Stars from 'react-native-stars';
import { Ionicons } from '@expo/vector-icons';


// instalar a biblioteca das starts : npm install react-native-stars --save

export default function Details(){
 const route = useRoute();
 const navigation = useNavigation();
    return(
            <View style ={styles.viewContainer}>

      
          

         
         
          <TouchableOpacity style ={styles.backButton} onPress={navigation.goBack}> 
          
          </TouchableOpacity>
          <View style={{marginTop:350}}>
        
            </View>
            <View style={{ width: "90%" }}>
        <FlatList
          horizontal={true}
          showsHorizontalScrollIndicator={false}
          data={lista1}
          keyExtractor={(item) => item.book_details[0].primary_isbn13}
          renderItem={({ item }) => {
            const book = item.book_details[0]; 
            return (
              <CardMovies
                titulo={book.title} 
                autor={book.author}  
                descricao={book.description}
                editora={book.publisher}
                faixaEtaria={book.age_group}
              />
            );
          }}
        />
      </View>
          <Text style ={styles.textBanner}> {route.params?.titulo}</Text>
            </View> 
    
        )


}
