import { StyleSheet, Dimensions } from 'react-native';

// Obtém a largura da tela
const screenWidth = Dimensions.get('window').width;

const styles = StyleSheet.create({
  card: {
    backgroundColor: '#ffffff',
    borderRadius: 8,
    // Sombra no React Native
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.1,
    shadowRadius: 8,
    width: screenWidth * 0.8, // Ajusta o tamanho do cartão para 80% da largura da tela
    overflow: 'hidden',
    flexDirection: 'column',
    marginVertical: 20, // Adicionado para espaçamento vertical
    alignSelf: 'center', // Centraliza horizontalmente
  },
  title: {
    textAlign: 'center',
    marginBottom: 10, // Ajusta o espaço abaixo do título
    color: '#333',
    fontSize: 18,
    fontWeight: 'bold',
  },
  dados_livro: {
    padding: 15,
  },
  textCommon: {
    textAlign: 'center', // Centraliza o texto
    marginVertical: 5,
    fontSize: 14,
    color: '#555',
    fontWeight: '500',
  },
  backButton: {
    width: 50,
    height: 50,
    backgroundColor: 'rgba(25,26,48,0.8)',
    borderRadius: 25,
    alignItems: 'center',
    justifyContent: 'center',
    position: 'absolute',
    top: 30, // Ajuste conforme necessário para a posição vertical
    left: 20, // Ajuste conforme necessário para a posição horizontal
  },
});

export default styles;
